import React from "react";
import { View, Image, StyleSheet } from "react-native";

import colors from "../config/colors";
import { AppText, ListItem } from "../components";
import { jacket, mosh } from "../assets/icons";

export const ListingDetailsScreen = () => (
    <View>
        <Image style={styles.image} source={jacket} />
        <View style={styles.container}>
            <AppText style={styles.title}>Red jacket for sale</AppText>
            <AppText style={styles.price}>$100</AppText>
            <View style={styles.userContainer}>
                <ListItem image={mosh} title="Kerry J. Young" subTitle="5 listing" />
            </View>
        </View>
    </View>
);

const styles = StyleSheet.create({
    container: {
        padding: 20,
    },
    title: {
        fontSize: 24,
        fontWeight: "500",
    },
    price: {
        marginVertical: 10,
        color: colors.secondary,
        fontWeight: "bold",
        fontSize: 20,
    },
    image: {
        width: "100%",
        height: 300,
    },
    userContainer: {
        marginVertical: 40,
    },
});
